package logicalProgramme;

public class PrintEachLetterDoubleInString {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String a = "hello";

		int j = a.length();
		for (int i = 0; i < j; i++) {
			char b = a.charAt(i);
			System.out.print(a.charAt(i));
			System.out.print(b);

		}

	}

}
